sys_variable_value.value:
(function parse(inputs, outputs) {

// Source item string is available in inputs.sourceItem.
// Generate output by setting fields of outputs.targetObject.
// 
// The structure of outputs.targetObject corresponds to the
// structure defined in the Data Stream Action output.
// 
// Optionally skip an item in the stream by setting
// outputs.state = 'SKIP'
//
// JSON Example:
// var item = JSON.parse(inputs.sourceItem);
// outputs.targetObject.ffName = item.firstName;
// outputs.targetObject.llName = item.lastName;
// outputs.targetObject.address.home.city = item.address.home.city;
//
// XML Example: 
// var xmlDoc = new XMLDocument2();
// xmlDoc.parseXML(inputs.sourceItem);
// var root = xmlDoc.getDocumentElement();
// if (root.getNodeName() != 'user') {
//     outputs.state = 'SKIP';
//     return;
// }
// outputs.targetObject.id = xmlDoc.getNodeText('/user/id');
// outputs.targetObject.fname = xmlDoc.getNodeText('/user/person/first_name');
// outputs.targetObject.lname = xmlDoc.getNodeText('/user/person/last_name');
    var glacier_rec = JSON.parse(inputs.sourceItem);
    outputs.targetObject.id = glacier_rec.glacier_id;
    outputs.targetObject.name = glacier_rec.name;
    outputs.targetObject.country = glacier_rec.country;
    outputs.targetObject.latitude = glacier_rec.latitude;
    outputs.targetObject.longitude = glacier_rec.longitude;
})(inputs, outputs);

